import { Component, OnInit } from '@angular/core';
import {MembreService} from '../services/membre.service';
import { IMembre } from '../models/IMembre';

@Component({
  selector: 'app-membre-list',
  templateUrl: './membre-list.component.html',
  styleUrls: ['./membre-list.component.css']
})
export class MembreListComponent implements OnInit {
    membres: IMembre[] | undefined;

    constructor(private membreService: MembreService) {
    }

    ngOnInit() {
      this.membreService.findAll().subscribe(data => {
        this.membres = data;
      });
    }
}
<div class="card my-5">
    <div class="card-body">
      <table class="table table-bordered table-striped">
        <thead class="thead-dark">
          <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let membre of membres">
            <td>{{ membre.id }}</td>
            <td>{{ membre.nom }}</td>
            <td><a href="mailto:{{ membre.email }}">{{ membre.email }}</a></td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
