          <tr *ngFor="let membre of membres">
            <td>{{ membre.id }}</td>
            <td>{{ membre.nom }}</td>
            <td>{{ membre.prenom }}</td>
            <td><a href="mailto:{{ membre.email }}">{{ membre.email }}</a></td>
          </tr>
        </tbody>
      </table>
    </div>
